    const dropdownItems = document.querySelectorAll('.dropdown-item');
    const contentDivs = document.querySelectorAll('[id^="content"]');

    // Show the clicked content and hide others
    dropdownItems.forEach(item => {
      const targetContentId = item.getAttribute('href');
      item.addEventListener('click', function(event) {
        event.preventDefault();
        contentDivs.forEach(div => {
          div.style.display = div.id === targetContentId.slice(1) ? 'block' : 'none';
        });
      });
    });

    // Close the dropdown if the user clicks outside of it
    window.addEventListener('click', function(event) {
      if (!event.target.matches('.dropdown-toggle')) {
        dropdownItems.forEach(item => {
          if (contentDivs.some(div => div.style.display === 'block')) {
            contentDivs.forEach(div => div.style.display = 'none');
          }
        });
      }
    });
